var linkMap = document.querySelector(".popup-big-map"),
  popupMap = document.querySelector(".pop-up-big-map"),
  closeMap = popupMap.querySelector(".close-map");
linkMap.addEventListener("click", function(p) {
  p.preventDefault(), popupMap.classList.add("pop-up-big-map-show");
}),
  closeMap.addEventListener("click", function(p) {
    p.preventDefault(), popupMap.classList.remove("pop-up-big-map-show");
  }),
  window.addEventListener("keydown", function(p) {
    27 === p.keyCode &&
      popupMap.classList.contains("pop-up-big-map-show") &&
      (p.preventDefault(), popupMap.classList.remove("pop-up-big-map-show"));
  });
